import React, {useEffect,useState} from 'react';
import { Header } from '../components/Header';
import { Sidebar } from '../components/Sidebar';
import { useAuth } from '../hooks/useAuth';
import { listParams, updateParam } from '../services/adminService';
import {TextField,Button,Box} from '@mui/material';

export function AdminPanel() {
  const {token} = useAuth();
  const [params,setParams] = useState<any[]>([]);

  const load = async() => {
    if(!token) return;
    const data = await listParams(token);
    setParams(data);
  }

  useEffect(()=>{load();},[token]);

  const handleUpdate = async (param_key:string,param_value:string) => {
    if(!token) return;
    await updateParam(token, param_key, param_value);
    await load();
  }

  return (
    <div style={{display:'flex'}}>
      <Sidebar/>
      <div style={{flex:1, padding:'20px'}}>
        <Header/>
        <h2>Admin Paneli</h2>
        <h3>Sistem Parametreleri</h3>
        {params.map(p=>(
          <Box key={p.id} sx={{display:'flex',gap:2,mb:2}}>
            <TextField label={p.param_key} defaultValue={p.param_value} onBlur={(e)=>handleUpdate(p.param_key,e.target.value)}/>
          </Box>
        ))}
      </div>
    </div>
  )
}
